import React, { useState } from "react";
import "./App.css";
import { GridRow } from "./components/GridRow";

function App() {
  const [rowCount, setRowCount] = useState(1);

  const handleButtonClick = () => {
    // Add logic to create rowCount number of GridRow components
    // For simplicity, I'm just logging the rowCount
    console.log("Creating", rowCount, "GridRow components");
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Thomas and Sharon's Initiative Tracker</h1>
      </header>
      <div className="App-body">
        <div className="grid">
          <div className="row">
            <div className="col">Player Name</div>
            <div className="col">Initiative</div>
            <div className="col">Movement</div>
            <div className="col">HP</div>
            <div className="col">AC</div>
            <div className="col">Conditions</div>
          </div>
          {/* Create input field and button */}
          <div>
            <input
              type="number"
              value={rowCount}
              onChange={(e) => setRowCount(parseInt(e.target.value))}
            />
            <button onClick={handleButtonClick}>Create Rows</button>
          </div>
          {/* Render GridRow components based on rowCount */}
          {[...Array(rowCount)].map((_, index) => (
            <GridRow key={index} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
