import React from "react";

export function GridRow() {
  // Function to handle input change and restrict to numbers
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    // Regular expression to allow only numeric input
    const regex = /^[0-9\b]+$/;

    // If input value matches the regex or it's an empty string,
    // update the state accordingly
    if (value === "" || regex.test(value)) {
      // Update state or perform any other action as needed
    }
  };

  // Function to clear initiative inputs
  const clearInitiativeInputs = () => {
    const initiativeInputs = document.getElementsByName("initiative");
    initiativeInputs.forEach((input) => {
      input.value = ""; // Clear input value
    });
  };

  return (
    <div className="row form-inline">
      <div className="col">
        <input
          className="form-control"
          name="initiative"
          type="number"
          onChange={handleInputChange}
        />
      </div>
      <div className="col">
        <input
          className="form-control"
          name="charactername"
          onChange={handleInputChange}
        />
      </div>
      <div className="col">
        <input
          className="form-control"
          name="movement"
          type="number"
          onChange={handleInputChange}
        />
      </div>
      <div className="col">
        <input
          className="form-control"
          name="hp"
          type="number"
          onChange={handleInputChange}
        />
      </div>
      <div className="col">
        <input
          className="form-control"
          name="ac"
          type="number"
          onChange={handleInputChange}
        />
      </div>
      <div className="col">
        <input
          className="form-control"
          name="conditions"
          onChange={handleInputChange}
        />
      </div>
    </div>
  );
}
