import React, { useState } from "react";
import "./App.css";
import { GridRow } from "./components/GridRow";

function App() {
  const [rowCount, setRowCount] = useState(1);
  const [createdRows, setCreatedRows] = useState([]);

  const handleButtonClick = () => {
    const newRows = [];
    for (let i = 1; i < rowCount; i++) {
      newRows.push(<GridRow key={i} />);
    }
    setCreatedRows(newRows);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Thomas and Sharon's Initiative Tracker</h1>
      </header>
      <div className="App-body">
        <div className="input-container">
          <input
            type="number"
            value={rowCount}
            onChange={(e) => setRowCount(parseInt(e.target.value))}
          />
          <button onClick={handleButtonClick}>Create Rows</button>
        </div>

        <div className="grid">
          <div className="row topRow">
            <div className="col">Player Name</div>
            <div className="col">Initiative</div>
            <div className="col">Movement</div>
            <div className="col">HP</div>
            <div className="col">AC</div>
            <div className="col">Conditions</div>
          </div>
          <GridRow key={0} />
          {createdRows.map((row, index) => (
            <div key={index} className="GridRow">
              {row}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
